var formatDate = function(detailsLevel) {
	detailsLevel = detailsLevel || 0;
	d = new Date();
	// Return only the date
	if (detailsLevel == 1) {
		return (d.toLocaleString().slice(9));
	}
	// Return only the time
	else if (detailsLevel == 2) {
		return (d.toLocaleString().slice(-8));
	}
	// Return both date and time
	else {
		return (d.toLocaleString());
	}
}

var randomColor = function () {
	var color = 'rgb(';
	for (var i = 0; i < 3; i++) {
		color += Math.floor((Math.random() * 25) * 10).toString() + (i == 2 ? '' : ', ');
	}
	color += ')'
	return color;
}

exports.formatDate = formatDate;
exports.randomColor = randomColor;